import React from 'react'
import SVG from 'react-inlinesvg'
import useWindowDimensions, {fullColorHex,scale} from "./Helpers";

export class EachCountry extends React.Component{
    constructor(props){
        super(props);
        this.state ={isSelected:false};
        this.handleSelected = this.handleSelected.bind(this);
    }

    handleSelected(){
        this.setState((prevState)=>{
            return {isSelected: !prevState.isSelected};
        });
    }

    render(){
        const {height, width} = useWindowDimensions();
        const {isSelected} = this.state;
        const svgPath = `https://storage.googleapis.com/ceramics/assets/${this.props.iso}.svg`;
        const countryStyle = {
            color: '#384E68',
            opacity: scale(this.props.objectCount,1,2616,0.1,1),
            top: scale(-this.props.latLon[0], -56.264,40.901,height/10, height/10*9),
            left: scale(this.props.latLon[1], -102.553,174.886,height/10, width-height/10),
            width: 56,
            height: 56
        };


        return(

                <SVG src={svgPath} onClick={this.handleSelected} style={countryStyle} uniquifyIDs={true}/>

        )
    }

}
