import React from 'react'
import SVG from 'react-inlinesvg'
import  {scale} from "./Helpers";
import styled, { css, keyframes } from "styled-components";
import Particles from "react-particles-js";
export class EachCountry extends React.Component{
    constructor(props){
        super(props);
        this.state ={isSelected:false};
        this.handleSelected = this.handleSelected.bind(this);
    }

    handleSelected(){
        this.setState((prevState)=>{
            return {isSelected: !prevState.isSelected};
        });
    }

    render(){
        const svgPath = `https://storage.googleapis.com/ceramics/assets/${this.props.iso}.svg`;
        const width = this.props.width;
        const height = this.props.height;
        const lat = this.props.latLon[0];
        const lon = this.props.latLon[1];
        const objectIDs =  this.props.objectIDs;
        const count = this.props.objectCount;
        const {isSelected} = this.state;
        const images = objectIDs.map(each=>{
            let img = {"src":`https://storage.googleapis.com/ceramics/crops/${each}.png`,
                       "height": 100,
                       "width":100};
            return img;
        })

        let imageCloud;
        if (isSelected){
            imageCloud=<Particles
                params={{
                    "particles": {
                        "number": {
                            "value": {count},
                            "density": {
                                "enable": true,
                                "value_area": 800
                            }
                        },
                        "line_linked": {
                            "enable": false
                        },
                        "move": {
                            "speed": 1,
                            "out_mode": "out"
                        },
                        "shape": {
                            "type": [
                                "images"
                            ],
                            "images": {images}
                        },
                        "color": {
                            "value": "#CCC"
                        },
                        "size": {
                            "value": {count},
                            "random": false,
                            "anim": {
                                "enable": true,
                                "speed": 4,
                                "size_min": 10,
                                "sync": false
                            }
                        }
                    },
                    "retina_detect": false
                }} />
        }

        const countryStyle = {
            opacity: scale(this.props.objectCount,1,2616,0.1,1),
            marginTop: Math.round(scale(-lat, -56.264,40.901,height/10, height/10*9)),
            marginLeft: Math.round(scale(lon, -102.553,174.886,height/10, width-height/10)),
            width: 100,
            height: 100,
        };

        const floating = keyframes`
            from {-webkit-transform:translate(0, 0px);}
            65% {-webkit-transform:translate(0, 50px);}
            to {-webkit-transform: translate(0, -0px);}`;
        const spinning = keyframes`
            0% {-webkit-transform: rotate(0deg);}
            100% {-webkit-transform: rotate(360deg);}`;
        const Float = styled.div`
            animation: ${spinning} 4s easy-in-out infinite`;

        console.log(Math.round(scale(-lat, -56.264,40.901,height/10, height/10*9)));
        return(

                <SVG className="float spin" src={svgPath} onClick={this.handleSelected} style={countryStyle} uniquifyIDs={true} preProcessor={code => code.replace(/fill=".*?"/g, 'fill="#384e68"')}/>

        )
    }

}
