import React from 'react'
import SVG from 'react-inlinesvg'
import {fullColorHex,scale} from "./Helpers";

export class EachCountry extends React.Component{
    constructor(props){
        super(props);
        this.state ={isSelected:false};
        this.handleSelected = this.handleSelected.bind(this);
    }

    handleSelected(){
        this.setState((prevState)=>{
            return {isSelected: !prevState.isSelected};
        });
    }

    render(){
        const h =window.innerHeight;
        const w = window.innerWidth;
        const {isSelected} = this.state;
        const svgPath = `https://storage.googleapis.com/ceramics/assets/${this.props.iso}.svg`;
        const countryStyle = {
            color: '#384E68',
            opacity: this.props.objectCount/2616,
            top: scale(-this.props.lat, -56.264,40.901,h/10, h/10*9),
            left: scale(this.props.lon, -102.553,174.886,h/10, w-h/10),
            width: 56,
            height: 56
        };


        return(

                <SVG className="float spin" src={svgPath} onClick={this.handleSelected} style={countryStyle} uniquifyIDs={true}/>

        )
    }

}
